const text = $input.first().json.message.text.toLowerCase();
const userId = $input.first().json.message.from.id;
const userName = $input.first().json.message.from.first_name;

let kategori = 'Pengeluaran';
let keterangan = '';
let jumlah = 0;

if (text.includes('pemasukan') || text.includes('gaji') || text.includes('bonus') || text.includes('dapet') || text.includes('dapat')) {
  kategori = 'Pemasukan';
}

const angkaMatch = text.match(/\d+[.,]?\d*/g);
if (angkaMatch) {
  jumlah = parseInt(angkaMatch[angkaMatch.length - 1].replace(/[.,]/g, ''));
}

keterangan = text
  .replace(/^(pemasukan|pengeluaran|beli|dapat|gaji|bonus|bayar)\s*/i, '')
  .replace(/\d+[.,]?\d*/g, '')
  .replace(/[|]/g, '')
  .trim();

let subKategori = 'Lain-lain';
if (kategori === 'Pengeluaran') {
  if (text.match(/kopi|makan|nasi|soto|warteg|restoran|jajan|sarapan|minum/i)) {
    subKategori = 'Makan & Minum';
  } else if (text.match(/bensin|parkir|grab|gojek|ojek|transport|motor|mobil/i)) {
    subKategori = 'Transportasi';
  } else if (text.match(/listrik|air|wifi|internet|pulsa|token|tv|kabel/i)) {
    subKategori = 'Tagihan';
  } else if (text.match(/baju|sepatu|celana|tas|belanja|toko|mall/i)) {
    subKategori = 'Belanja';
  } else if (text.match(/nonton|bioskop|game|spotify|netflix|youtube|hiburan/i)) {
    subKategori = 'Hiburan';
  } else if (text.match(/dokter|obat|rumah sakit|vitamin|kesehatan/i)) {
    subKategori = 'Kesehatan';
  }
  kategori = `${kategori}-${subKategori}`;
}

return [{
  json: {
    tanggal: new Date().toISOString().split('T')[0],
    waktu: new Date().toTimeString().split(' ')[0].substring(0, 5),
    keterangan: keterangan || 'Tidak ada keterangan',
    kategori: kategori,
    jumlah: jumlah,
    user: userName,
    userId: userId,
    rawText: text
  }
}];

const transaction = $('Save to Google Sheets').first().json;
const parseData = $('Parse Input').first().json;
const budgetItems = $input.all();
const userId = parseData.userId;

// Build budget map for this user
const budgets = {};
budgetItems.forEach(item => {
  const itemUserId = String(item.json['User ID'] || '');
  const kategori = item.json.Kategori || '';
  const amount = parseInt(item.json['Budget Amount'] || 0);
  const period = (item.json.Period || 'monthly').toLowerCase();

  if (itemUserId === String(userId)) {
    const fullKategori = `Pengeluaran-${kategori}`;
    budgets[fullKategori] = {
      amount: amount,
      period: period
    };
  }
});

let alertMessage = '';
const kategori = parseData.kategori || '';
const jumlah = parseData.jumlah || 0;

if (kategori && typeof kategori === 'string' && kategori.includes('Pengeluaran')) {
  if (budgets[kategori]) {
    const budget = budgets[kategori].amount;
    const period = budgets[kategori].period;
    const percentage = (jumlah / budget) * 100;

    if (percentage >= 80) {
      const subKategori = kategori.split('-')[1] || kategori;
      alertMessage = `\n\n‚ö†Ô∏è *PERINGATAN BUDGET*\nKategori "${subKategori}" sudah mencapai ${percentage.toFixed(0)}% dari budget ${period}!\nüí∞ Budget: Rp ${budget.toLocaleString('id-ID')}\nüí∏ Transaksi ini: Rp ${jumlah.toLocaleString('id-ID')}\n‚úÖ Sisa budget: Rp ${Math.max(0, budget - jumlah).toLocaleString('id-ID')}`;
    }
  }
}

return [{
  json: {
    ...parseData,
    ...transaction,
    alertMessage: alertMessage
  }
}];

const items = $input.all();
const triggerData = $('Telegram Trigger').first().json;

let totalPemasukan = 0;
let totalPengeluaran = 0;
let transaksiPemasukan = [];
let transaksiPengeluaran = [];
let kategoriPengeluaran = {};

items.forEach(item => {
  const kategori = item.json.Kategori || '';
  const jumlah = parseInt(item.json.Jumlah || 0);

  if (kategori.includes('Pemasukan')) {
    totalPemasukan += jumlah;
    transaksiPemasukan.push({
      tanggal: item.json.Tanggal,
      keterangan: item.json.Keterangan,
      jumlah: jumlah
    });
  } else {
    totalPengeluaran += jumlah;
    transaksiPengeluaran.push({
      tanggal: item.json.Tanggal,
      keterangan: item.json.Keterangan,
      kategori: kategori,
      jumlah: jumlah
    });

    if (!kategoriPengeluaran[kategori]) {
      kategoriPengeluaran[kategori] = 0;
    }
    kategoriPengeluaran[kategori] += jumlah;
  }
});

const saldo = totalPemasukan - totalPengeluaran;
const persentasePengeluaran = totalPemasukan > 0 
  ? ((totalPengeluaran / totalPemasukan) * 100).toFixed(1)
  : 0;

const topKategori = Object.entries(kategoriPengeluaran)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3);

const pemasukanTerbesar = transaksiPemasukan.sort((a, b) => b.jumlah - a.jumlah)[0] || {};
const pengeluaranTerbesar = transaksiPengeluaran.sort((a, b) => b.jumlah - a.jumlah)[0] || {};

return [{
  json: {
    totalPemasukan,
    totalPengeluaran,
    saldo,
    persentasePengeluaran,
    jumlahTransaksi: items.length,
    topKategori,
    pemasukanTerbesar,
    pengeluaranTerbesar,
    transaksiTerakhir: items[items.length - 1]?.json || {},
    // Preserve Telegram data
    telegramUserId: triggerData.message.from.id,
    telegramChatId: triggerData.message.chat.id
  }
}];

const text = $input.first().json.message.text.toLowerCase();
let startDate, endDate;
const today = new Date();

if (text.includes('hari ini') || text.includes('today')) {
  startDate = endDate = today.toISOString().split('T')[0];
} else if (text.includes('minggu ini') || text.includes('week')) {
  const firstDay = new Date(today.setDate(today.getDate() - today.getDay()));
  const lastDay = new Date(today.setDate(today.getDate() - today.getDay() + 6));
  startDate = firstDay.toISOString().split('T')[0];
  endDate = lastDay.toISOString().split('T')[0];
} else if (text.includes('bulan ini') || text.includes('month')) {
  startDate = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().split('T')[0];
  endDate = new Date(today.getFullYear(), today.getMonth() + 1, 0).toISOString().split('T')[0];
} else if (text.includes('7 hari') || text.includes('seminggu')) {
  startDate = new Date(today.setDate(today.getDate() - 7)).toISOString().split('T')[0];
  endDate = new Date().toISOString().split('T')[0];
} else if (text.includes('30 hari') || text.includes('sebulan')) {
  startDate = new Date(today.setDate(today.getDate() - 30)).toISOString().split('T')[0];
  endDate = new Date().toISOString().split('T')[0];
} else {
  startDate = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().split('T')[0];
  endDate = new Date(today.getFullYear(), today.getMonth() + 1, 0).toISOString().split('T')[0];
}

return [{
  json: {
    startDate,
    endDate,
    periodText: text
  }
}];

const items = $input.all();
const filterData = $('Filter Period Parser').first().json;
const startDate = filterData.startDate;
const endDate = filterData.endDate;

const filtered = items.filter(item => {
  const itemDate = item.json.Tanggal;
  return itemDate >= startDate && itemDate <= endDate;
});

return filtered.map(item => ({ json: item.json }));

