const transaction = $input.first().json;
const waktu = transaction.waktu;
const hour = parseInt(waktu.split(':')[0]);

const isLateNight = hour >= 21 || hour <= 5;

const keteranganLower = (transaction.keterangan || '').toLowerCase();

const dangerousKeywords = {
  spicy: ['pedas', 'seblak', 'mie pedas', 'sambal', 'rica', 'cabe'],
  acidic: ['kopi', 'espresso', 'asam', 'jeruk', 'lemon'],
  fried: ['gorengan', 'goreng', 'ayam goreng', 'tempe goreng'],
  fastFood: ['mcdonald', 'kfc', 'burger', 'pizza', 'kebab'],
  soda: ['coca cola', 'pepsi', 'sprite', 'fanta', 'soda'],
  junkFood: ['snack', 'keripik', 'chitato', 'doritos']
};

const safeKeywords = {
  healthy: ['bubur', 'oatmeal', 'sup', 'rebus', 'kukus', 'tim', 'sayur'],
  medicine: ['obat', 'vitamin', 'suplemen', 'antasida']
};

let dangerLevel = 0;
let dangerTypes = [];

Object.entries(dangerousKeywords).forEach(([type, keywords]) => {
  keywords.forEach(keyword => {
    if (keteranganLower.includes(keyword)) {
      dangerLevel++;
      if (!dangerTypes.includes(type)) {
        dangerTypes.push(type);
      }
    }
  });
});

let isSafeFood = false;
Object.entries(safeKeywords).forEach(([type, keywords]) => {
  keywords.forEach(keyword => {
    if (keteranganLower.includes(keyword)) {
      isSafeFood = true;
    }
  });
});

if (isLateNight && !isSafeFood) {
  dangerLevel += 1;
  dangerTypes.push('lateNight');
}

return [{
  json: {
    ...transaction,
    isLateNight: isLateNight,
    dangerLevel: dangerLevel,
    dangerTypes: dangerTypes,
    isSafeFood: isSafeFood,
    healthContext: {
      time: isLateNight ? 'LATE_NIGHT_WARNING' : 'SAFE_TIME',
      foodSafety: isSafeFood ? 'SAFE' : dangerLevel > 0 ? 'DANGEROUS' : 'NEUTRAL',
      urgency: dangerLevel >= 2 ? 'HIGH' : dangerLevel === 1 ? 'MEDIUM' : 'LOW'
    }
  }
}];

const allTransactions = $input.all();
const currentTransaction = $('Parse Input').first().json;
const userId = currentTransaction.userId;

const last7Days = new Date();
last7Days.setDate(last7Days.getDate() - 7);
const last7DaysStr = last7Days.toISOString().split('T')[0];

const recentTransactions = allTransactions.filter(t => 
  t.json.Tanggal >= last7DaysStr && 
  t.json.User === currentTransaction.user
);

const categoryCount = {};
const categoryHistory = {};
let lateNightCount = 0;
let totalRecent = 0;

recentTransactions.forEach(t => {
  const category = t.json.Kategori || 'Lain-lain';
  const time = t.json.Waktu || '';
  const hour = parseInt(time.split(':')[0]);

  categoryCount[category] = (categoryCount[category] || 0) + 1;

  if (!categoryHistory[category]) {
    categoryHistory[category] = [];
  }
  categoryHistory[category].push({
    date: t.json.Tanggal,
    amount: t.json.Jumlah,
    desc: t.json.Keterangan
  });

  if (hour >= 21 || hour <= 5) {
    lateNightCount++;
  }

  totalRecent++;
});

const topCategories = Object.entries(categoryCount)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3)
  .map(([cat, count]) => ({ category: cat, frequency: count }));

const frequentDangerousItems = [];
const dangerousKeywords = {
  'kopi': 'Kopi',
  'pedas': 'Makanan Pedas',
  'goreng': 'Gorengan',
  'fast food': 'Fast Food',
  'soda': 'Soda/Softdrink',
  'mie instan': 'Mie Instan'
};

recentTransactions.forEach(t => {
  const desc = (t.json.Keterangan || '').toLowerCase();
  Object.entries(dangerousKeywords).forEach(([keyword, name]) => {
    if (desc.includes(keyword)) {
      const existing = frequentDangerousItems.find(item => item.name === name);
      if (existing) {
        existing.count++;
      } else {
        frequentDangerousItems.push({ name, count: 1 });
      }
    }
  });
});

const memoryContext = {
  totalTransactionsLast7Days: totalRecent,
  lateNightTransactions: lateNightCount,
  topCategories: topCategories,
  frequentDangerousItems: frequentDangerousItems.sort((a, b) => b.count - a.count),
  categoryHistory: categoryHistory,
  lastTransactionDate: recentTransactions[recentTransactions.length - 1]?.json?.Tanggal || 'N/A',
  memoryText: `Dalam 7 hari terakhir, Ayah telah melakukan ${totalRecent} transaksi. Kategori terbanyak adalah ${topCategories[0]?.category || 'Umum'} (${topCategories[0]?.frequency || 0}x). Transaksi tengah malam: ${lateNightCount}x. Item berbahaya yang sering dibeli: ${frequentDangerousItems.slice(0, 2).map(i => i.name + ' (' + i.count + 'x)').join(', ') || 'Tidak ada'}.`
};

return [{
  json: {
    ...currentTransaction,
    memoryContext: memoryContext,
    spendingPattern: memoryContext.memoryText,
    frequentCategories: topCategories,
    historicalData: categoryHistory
  }
}];
